    `tdiscovery_apps_tasks_macros`.`id_task` = `trecon_task`.`id_rt` AND
    `trecon_task`.`type` = @current_app_type AND
    `tdiscovery_apps_tasks_macros`.`macro` = '_sapLicense_' AND
